mapper.select:
SELECT

FROM `tc_active` act
WHERE act.`act_type` = #{actType}
AND act.`state` = '2' AND NOW() > act.`start_time` AND act.`end_time` > NOW()

	AND act.user_id = #{userId}


	AND act.cate_id = #{cateId}


	AND act.region = #{region}


	AND act.addName = #{addName}

SELECT

FROM `tc_active` act
WHERE act.`state` = '1' AND act.user_id = #{userId}

	act.`act_type` = #{actType}

ORDER BY act.create_time DESC

SELECT COUNT(0) FROM `tc_active` WHERE user_id = #{userId} AND FIND_IN_SET(act_type, #{actType})

	AND state = #{state}


	AND state != 0
